def get_email(username, email=''):
    print("Removing report.pdf, data charts from network storage ")
    email_query = input('Do you want report.pdf to be sent via email? (y/n): ').strip().lower()
    if email_query == 'y':
        if email == '':
            email = input('Enter your email id to receive report.pdf: ').strip().lower()
            while '@' not in email:
                print("'@' missing in email")
                email = input('Enter your email id to receive report.pdf: ').strip().lower()

        send_email(username, email)
    else:
        pass



